package hihoCoder;

import java.util.Scanner;

public class AplusB {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		while(in.hasNext()){
			int x1 = in.nextInt();
			int x2 = in.nextInt();
			int x1pliusx2 = x1 + x2;
			System.out.println(x1pliusx2);
		}

	}

}
